package pattern;

public class Cal4 {
	//with input with output
	int add(int a,int b)
	{
		int c=a+b;
		return c;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Cal4 a = new Cal4();
		int x=50;
		int y=40;
		int res=a.add(x, y);
		System.out.println(res);
		
		
			
		

	}

}
